package projectEuler;

/**
 * Find the greatest product of 5 consecutive digits in a number
 * @author Danny
 *
 */
public class Problem8 {
	public static void main(String[] args) {
		Problem8 p8 = new Problem8();
		System.out.println(p8.greatestProduct(""));
	}
	
	public int greatestProduct(String sNum) {
		int numLen = sNum.length();
		int greatestProduct = 0;
		int current = 0; //store current number for comparison 
		
		for (int i = 0; i < numLen - 4; i++) {
			current = getProduct(sNum, i);
			if (greatestProduct == 0) {
				greatestProduct = current;
			}
			else if (current > greatestProduct) {
				greatestProduct = current;
			}
		}
		return greatestProduct;
	}
	
	public int getProduct(String sNum, int start) {
		int i = start;
		int product = 0;
		
		int first = Integer.parseInt(sNum.substring(i, i+1));
		int second = Integer.parseInt(sNum.substring(i+1, i+2));
		int third = Integer.parseInt(sNum.substring(i+2, i+3));
		int fourth = Integer.parseInt(sNum.substring(i+3, i+4));
		int fifth = Integer.parseInt(sNum.substring(i+4, i+5));
		
		product = first * second * third * fourth * fifth;
		//System.out.print(" " + product);
		return product;
	}
}
